import 'package:flutter/material.dart';

void main() {
  runApp(const WidgetApp());
}

class WidgetApp extends StatelessWidget {
  const WidgetApp({super.key});

  @override
  Widget build(BuildContext context) {
    return MaterialApp(
      title: 'Widget App',
      theme: ThemeData(primarySwatch: Colors.blue),
      home: const MyHomePage(),
    );
  }
}

class MyHomePage extends StatelessWidget {
  const MyHomePage({super.key});

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(title: const Text('Common Widgets in Flutter')),
      body: Column(
        children: <Widget>[
          // Display an image at the top
          Image.network(
            'data:image/jpeg;base64,', // Replace with your image URL
            width: double.infinity,
            height: 200,
            fit: BoxFit.cover,
          ),
          const SizedBox(height: 20), // Add some space
          // Row with icons and text
          Row(
            mainAxisAlignment: MainAxisAlignment.spaceEvenly,
            children: <Widget>[
              Icon(Icons.favorite, size: 40, color: Colors.red),
              const Text('Flutter Widgets', style: TextStyle(fontSize: 20)),
              Icon(Icons.favorite, size: 40, color: Colors.red),
            ],
          ),
          const SizedBox(height: 20), // Add some space
          // Elevated button at the bottom
          ElevatedButton(
            onPressed: () {
              // Action when button is pressed
              ScaffoldMessenger.of(
                context,
              ).showSnackBar(const SnackBar(content: Text('Button Pressed!')));
            },
            child: const Text('Click Me'),
          ),
        ],
      ),
    );
  }
}
